    sequelize,
    modelName: 'Comment',
  });

    Comment.associate = (models) => {
      Comment.belongsTo(models.Users,{
        foreignKey: "UID"
      });
      Comment.belongsTo(models.Board, {
        foreignKey: "BID"
      });
    };




  return Comment;
};

